import React, { useEffect, useRef, useState } from "react";
import "./Menu.css";

const Menu = () => {
  const marqueeRef = useRef(null);
  const marqueeContainerRef = useRef(null);
  const [categories, setCategories] = useState([]); // State untuk menyimpan kategori makanan
  const [searchTerm, setSearchTerm] = useState(""); // State untuk menyimpan input pencarian
  const [selectedCategory, setSelectedCategory] = useState(""); // State untuk menyimpan kategori yang dipilih
  const [loading, setLoading] = useState(true); // State untuk loading

  // Mapping kategori API ke kategori yang lebih umum
  const categoryMapping = {
    Meat: ["Beef", "Chicken", "Pork", "Lamb"],
    Vegetarian: ["Vegetarian"],
    "Makanan Laut": ["Seafood"],
    Dessert: ["Dessert", "Pudding"],
    Drink: ["Drink"],
  };

  useEffect(() => {
    const fetchCategories = async () => {
      setLoading(true);
      try {
        const response = await fetch(
          "https://www.themealdb.com/api/json/v1/1/categories.php"
        );
        const data = await response.json();
        setCategories(data.categories); // Simpan kategori ke state
      } catch (error) {
        console.error("Error fetching categories:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    const marquee = marqueeRef.current;
    const marqueeContainer = marqueeContainerRef.current;

    // Duplicate the content for a seamless infinite effect
    const content = marquee.innerHTML;
    marquee.innerHTML += content; // Duplicate content to create the loop effect

    // Set the width of the marquee container based on the content
    const contentWidth = marquee.offsetWidth;
    marqueeContainer.style.width = `${contentWidth}px`;
  }, []);

  // Filter categories based on search term and selected category
  const filteredCategories = categories.filter((category) => {
    const matchesSearchTerm = category.strCategory
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchesSelectedCategory = selectedCategory
      ? categoryMapping[selectedCategory]?.includes(category.strCategory)
      : true;
    return matchesSearchTerm && matchesSelectedCategory;
  });

  // Reset Filters
  const resetFilters = () => {
    setSelectedCategory("");
    setSearchTerm("");
  };

  return (
    <section id="menu" className="menu">
      <div className="marquee">
        <div ref={marqueeContainerRef} className="marquee-container">
          <div ref={marqueeRef} className="marquee-content">
            <span>OUR MENU • </span>
            <span>OUR MENU • </span>
            <span>OUR MENU • </span>
          </div>
        </div>
      </div>

      {/* Dropdown Filter */}
      <div className="filter-container">
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="filter-select"
        >
          <option value="">All Categories</option>
          {Object.keys(categoryMapping).map((key) => (
            <option key={key} value={key}>
              {key}
            </option>
          ))}
        </select>
        <button onClick={resetFilters} className="reset-button">
          Reset
        </button>
      </div>

      {/* Input Pencarian */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for a category..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      {/* Loading State */}
      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="menu-grid">
          {filteredCategories.map((category) => (
            <div key={category.idCategory} className="menu-card">
              <img src={category.strCategoryThumb} alt={category.strCategory} />
              <h3>{category.strCategory}</h3>
              <p>{category.strCategoryDescription.split(". ")[0]}.</p>{" "}
              {/* Menampilkan deskripsi hingga titik pertama */}
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default Menu;
