import React, { useEffect, useRef, useState } from "react";
import "./Menu.css";

const Menu = () => {
  const marqueeRef = useRef(null);
  const marqueeContainerRef = useRef(null);
  const [categories, setCategories] = useState([]); // State untuk menyimpan kategori makanan
  const [searchTerm, setSearchTerm] = useState(""); // State untuk menyimpan input pencarian

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(
          "https://www.themealdb.com/api/json/v1/1/categories.php"
        );
        const data = await response.json();
        setCategories(data.categories); // Simpan kategori ke state
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    const marquee = marqueeRef.current;
    const marqueeContainer = marqueeContainerRef.current;

    // Duplicate the content for a seamless infinite effect
    const content = marquee.innerHTML;
    marquee.innerHTML += content; // Duplicate content to create the loop effect

    // Set the width of the marquee container based on the content
    const contentWidth = marquee.offsetWidth;
    marqueeContainer.style.width = `${contentWidth}px`;
  }, []);

  // Filter categories based on search term
  const filteredCategories = categories.filter((category) =>
    category.strCategory.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section id="menu" className="menu">
      <div className="marquee">
        <div ref={marqueeContainerRef} className="marquee-container">
          <div ref={marqueeRef} className="marquee-content">
            <span>OUR MENU • </span>
            <span>OUR MENU • </span>
            <span>OUR MENU • </span>
          </div>
        </div>
      </div>

      {/* Input Pencarian */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for a category..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      <div className="menu-grid">
        {filteredCategories.map((category) => (
          <div key={category.idCategory} className="menu-card">
            <img src={category.strCategoryThumb} alt={category.strCategory} />
            <h3>{category.strCategory}</h3>
            <p>{category.strCategoryDescription.split(". ")[0]}.</p>{" "}
            {/* Menampilkan deskripsi hingga titik pertama */}
          </div>
        ))}
      </div>
    </section>
  );
};

export default Menu;
