import React, { useEffect, useState, useRef } from "react";
import "./Menu.css";

const Menu = () => {
  const [categories, setCategories] = useState([]);
  const marqueeRef = useRef(null);

  useEffect(() => {
    // Fetch data dari API
    fetch("https://www.themealdb.com/api/json/v1/1/categories.php")
      .then((response) => response.json())
      .then((data) => setCategories(data.categories));

    // Setup animasi marquee manual untuk scrolling yang halus
    const marquee = marqueeRef.current;
    let startPos = 0;

    const animate = () => {
      if (startPos <= -marquee.scrollWidth / 2) {
        startPos = 0; // Reset posisi jika sudah selesai satu siklus
      }
      startPos -= 1; // Kecepatan pergerakan
      marquee.style.transform = `translateX(${startPos}px)`;
      requestAnimationFrame(animate);
    };

    animate(); // Mulai animasi marquee
  }, []);

  return (
    <section id="menu" className="menu">
      {/* Marquee */}
      <div className="marquee">
        <div className="marquee-content" ref={marqueeRef}>
          <span>OUR MENU • </span>
          <span>OUR MENU • </span>
          <span>OUR MENU • </span>
          <span>OUR MENU • </span>
        </div>
      </div>

      {/* Daftar Menu */}
      <div className="menu-grid">
        {categories.map((category) => (
          <div key={category.idCategory} className="menu-card">
            <img src={category.strCategoryThumb} alt={category.strCategory} />
            <h3>{category.strCategory}</h3>
            <p>{category.strCategoryDescription.substring(0, 100)}...</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Menu;
